mapper.select:
select id, userId, subject, content, imagePath, createdAt, updatedAt from post where userId = #{userId}
order by `id` desc
and `id` > #{standardId}
order by `id` asc
and `id` < #{standardId}
order by `id` asc
limit ${limit}

mapper.update:
update
post
set
subject = #{subject},
content = #{content},
updatedAt = now()


,imagePath = #{imagePath}

where
id = #{postId}
and userId = #{userId}
